const menu=document.querySelector(".main-nav"),openMenuButton=menu.querySelector(".main-nav__toggle"),openClassName="main-nav--opened";function openMenu(){menu.classList.add(openClassName)}function closeMenu(){menu.classList.remove(openClassName)}menu.classList.remove("main-nav--no-js"),openMenuButton.addEventListener("click",function(e){e.preventDefault(),menu.classList.contains(openClassName)?closeMenu():openMenu()}),window.addEventListener("keydown",function(e){27===e.keyCode&&closeMenu()});